# Script used to generate expressions
#
# Finds expressions by assuming an interpolating polynomial
# goes through the points. From (20) in the paper
# some cases take a **long** time to run.
# At some point, this gets to be more of an academic exercise than
# a performant solution

#= -------
using SymPy
@vars y
xs = [Sym("x$i") for i ∈ 0:20]
fs = [Sym("f$i") for i ∈ 0:20]
f′s = [Sym("f′$i") for i ∈ 0:20]
f′′s = [Sym("f′′$i") for i ∈ 0:20]
f′′′s = [Sym("f′′′$i") for i ∈ 0:20]
f′′′′s = [Sym("f′′′′$i") for i ∈ 0:20]
hs = [Sym("h$i") for i ∈ 0:20]
h0 = first(hs)

## f,f', f''
function test(s=2,d=2)
    H(y) = sum(hs[i+1]*(y-fs[s])^i for i ∈ 0:(1+d)*s-1)
  
    H′ = diff(H(y),y)
    H′′ = diff(H′,y)
    H′′′ = diff(H′′,y)
    H′′′′ = diff(H′′′,y)

eqs = [subs(H(fs[i]), Dict(h0=>xs[s])) - xs[i] for i ∈ 1:s-1]
if d >= 1
    for i ∈ 1:s
        push!(eqs, subs(H′, Dict(y=>fs[i], h0=>xs[s])) - 1/f′s[i])
    end
end
if d >= 2
    for i ∈ 1:s
        push!(eqs, subs(H′′, Dict(y=>fs[i], h0=>xs[s])) - -f′′s[i]/f′s[i]^3)
    end
end
if d >= 3
    for i ∈ 1:s
        push!(eqs, subs(H′′′, Dict(y=>fs[i], h0=>xs[s])) -
              -f′′′s[i]/(f′s[i]^4) + 3 * f′′s[i] / (f′s[i]^5))
    end
end
if d >= 4
    for i ∈ 1:s
        f′,f′′,f′′′,f′′′′ = f′s[i],f′′s[i],f′′′s[i],f′′′′s[i]
        g′     =     1/f′
        g′′    = - (1/f′^2) * g′* f′′ 
        g′′′   = - (1/f′^3) * (g′′*3f′′*f′ + g′*f′′′)
        g′′′′  = - (1/f′^4)  * (g′′′ * 6 * (f′^2) * f′′ +
                                g′′  * (4 * f′′′* f′ + (f′′)^2) +
                                g′ * f′′′′)
        push!(eqs, subs(H′′′′, Dict(y=>fs[i], h0=>xs[s])) - g′′′′)
    end
end

ϕ = sympy.linsolve(eqs[1:(d+1)*s-1], hs[2:(d+1)*s]...)
ϕ = first(elements(ϕ))
ϕ = Sym.(convert(Tuple, ϕ.__pyobject__))
D = Dict{Any,Any}(h0=>xs[s])
for i in 1:(d+1)*s-1
    D[hs[i+1]] = ϕ[i]
end
subs(H(0), D) |> simplify
end
=#


 
